function generateVcode($vcode=null){
	$db = getDbInstance();
	$db->where ("vcode", $vcode);
    $row = $db->get('admin_accounts');
	if ($db->count >= 1){
		$getVcode= rand(100000,999999);
		return generateVcode($getVcode);
	} 
	return $vcode;
}

